// SkillModifiers.js
import React from 'react';
import { SKILL_LIST } from "../consts";

const Skill = ({ attributes }) => {
  // Get the attribute value from the attributes state
  const getAttributeValue = (attributeName) => {
    const attribute = attributes.find(attr => attr.name === attributeName);
    return attribute ? attribute.value : 0;
  };

  return (
    <div className="p-4 space-y-4">
      <h2 className="text-xl font-bold">Skill Modifiers</h2>
      {SKILL_LIST.map(skill => (
        <div
          key={skill.name}
          className="flex items-center justify-between p-4 border border-gray-300 rounded-lg"
        >
          <span className="w-48 font-medium">{skill.name}</span>
          <span className="w-32 text-right">Modifier: {skill.attributeModifier}</span>
          <span className="w-16 text-center">
            {/* Display the corresponding attribute value */}
            {getAttributeValue(skill.attributeModifier)}
          </span>
        </div>
      ))}
    </div>
  );
};

export default Skill;
