import React, { useEffect, useState } from 'react';
import { SKILL_LIST } from "../consts";

const Skill = ({ attributes }) => { // Assuming attributes are passed as props
  const getInitialModifiers = () => {
    const storedModifiers = localStorage.getItem('skillModifiers');
    if (storedModifiers) {
      return JSON.parse(storedModifiers);
    }
    const list = SKILL_LIST.map((skill) => ({
      name: skill.name,
      modifier: 0,
      attributeModifier: skill.attributeModifier,
    }));
    localStorage.setItem('skillModifiers', JSON.stringify(list));
    return list; // Return initial list if no stored modifiers
  };

  const [skills, setSkills] = useState(getInitialModifiers);

  // Update localStorage whenever skills change
  useEffect(() => {
    localStorage.setItem('skillModifiers', JSON.stringify(skills));
  }, [skills]);

  useEffect(() => {
    const handleStorageChange = (event) => {
      if (event.key === 'skillModifiers') {
        const newModifiers = JSON.parse(event.newValue);
        setSkills(newModifiers || getInitialModifiers());
      }
    };

    window.addEventListener('storage', handleStorageChange);

    // Cleanup the event listener on component unmount
    return () => {
      window.removeEventListener('storage', handleStorageChange);
    };
  }, []);

  // Function to update skill modifiers based on attributes
  const updateSkillModifiers = () => {
    const updatedSkills = skills.map(skill => {
      const associatedAttribute = attributes.find(attr => attr.name === skill.attributeModifier);
      let modifierValue = skill.modifier;

      if (associatedAttribute) {
        const attrValue = associatedAttribute.value;
        if (attrValue > 10) {
          const newModifierValue = Math.floor((attrValue - 10) / 2);
          if (newModifierValue !== modifierValue) {
            modifierValue = newModifierValue;
          }
        } else {
          modifierValue = 0;
        }
      }

      return { ...skill, modifier: modifierValue };
    });

    setSkills(updatedSkills); // Update state with new modifiers
  };

  // Example call to updateSkillModifiers whenever needed
  useEffect(() => {
    updateSkillModifiers(); // Call this whenever the attributes change
  }, [attributes]); // This will ensure that the modifiers are recalculated if attributes change

  const handleIncrement = (index) => {
    const updatedSkills = [...skills];
    updatedSkills[index].modifier += 1;
    setSkills(updatedSkills);
  };

  const handleDecrement = (index) => {
    const updatedSkills = [...skills];
    if (updatedSkills[index].modifier > -5) {
      updatedSkills[index].modifier -= 1;
      setSkills(updatedSkills);
    }
  };

  return (
    <div className="p-4 space-y-4">
      <h2 className="text-xl font-bold">Skill Modifiers</h2>
      {skills.map((skill, index) => (
        <div
          key={skill.name}
          className="flex items-center justify-between p-4 border border-gray-300 rounded-lg"
        >
          <span className="w-48 font-medium">{skill.name}</span>
          <span className="w-32">{skill.modifier}</span>
          <div className="flex items-center space-x-4">
            <button
              className="px-3 py-1 bg-red-500 text-white rounded hover:bg-red-600"
              onClick={() => handleDecrement(index)}
              disabled={skill.modifier <= -5}
            >
              -
            </button>
            <button
              className="px-3 py-1 bg-green-500 text-white rounded hover:bg-green-600"
              onClick={() => handleIncrement(index)}
            >
              +
            </button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Skill;
