import React from 'react';
import { CLASS_LIST } from "../consts";
import { useSelector } from "react-redux";

const ClassAttribute = () => {
  // Get the current class from the Redux store
  let currentClass = useSelector((state) => state.classSlice.CurrentClass);

  // Get the attributes for the current class
  const attributes = CLASS_LIST[currentClass];

  return (
    currentClass && attributes && (
      <div className="p-4 space-y-4">
        <h2 className="text-xl font-bold"> {currentClass} Attribute List</h2>
        {Object.keys(attributes).map((attrName) => (
          <div
            key={attrName}
            className="flex items-center justify-between p-4 border border-gray-300 rounded-lg"
          >
            <span className="w-32 font-medium">{attrName}</span>
            <span className="w-8 text-center text-lg">{attributes[attrName]}</span>
          </div>
        ))}
      </div>
    )
  );
};

export default ClassAttribute;
